    //////for testing//////




    function isValidSignature(uint256 amount, bytes signature, address sender) internal returns (bool){
        bytes32 message = prefixed(keccak256(this, amount));

        // Check that the signature is from the payment sender.
        return recoverSigner(message, signature) == sender;
    }

    function splitSignature(bytes sig) internal pure returns (uint8, bytes32, bytes32) {
        require(sig.length == 65);

        bytes32 r;
        bytes32 s;
        uint8 v;

        assembly {
            // first 32 bytes, after the length prefix
            r := mload(add(sig, 32))
            // second 32 bytes
            s := mload(add(sig, 64))
            // final byte (first byte of the next 32 bytes)
            v := byte(0, mload(add(sig, 96)))
        }
        return (v, r, s);
    }

    function recoverSigner(bytes32 message, bytes sig) internal pure returns (address) {
        uint8 v;
        bytes32 r;
        bytes32 s;

        (v, r, s) = splitSignature(sig);

        return ecrecover(message, v, r, s);
    }

    // Builds a prefixed hash to mimic the behavior of eth_sign.
    function prefixed(bytes32 hash) internal pure returns (bytes32) {
        return keccak256("\x19Ethereum Signed Message:\n32", hash);

    }












    uint32 value = 10;
    bytes32 tradeHash ;
    uint32 number = 0;

    function getValue()  external view returns (uint32,bytes32)  {
        return (value,tradeHash);
    }

    function getTimestamp() external view returns (uint32){
       return uint32(block.timestamp);
    }

    function getEscrowData(bytes32 _tradeHash)  external view returns (bool, uint32,uint32)  {
        Escrow memory escrow =  escrows[_tradeHash];
        return (escrow.exists , escrow.sellerCanCancelAfter, requestCancellationMinimumTime);
    }

    function getEscrowNumber()  external view returns (uint32)  {
        return number;
    }


}

