pragma solidity ^0.4.18;
import 'zeppelin-solidity/contracts/token/ERC20/ERC20.sol';

contract SimpleTokenMarket {

   struct Escrow{
      uint256 expiry;
      uint256 amount;
   }

   function SimpleTokenMarket() public{

   }

   mapping (address => Escrow) public balances;

   modifier nonZeroAddress(address serviceProvider) {
       require(serviceProvider != 0x0);
       _;
   }

   modifier nonZeroNumber(uint number) {
       require(number > 0);
       _;
   }

   modifier senderHasApprovedTransfer(ERC20 token, uint amount) {
       require(token.allowance(msg.sender, this) >= amount);
       _;
   }

   modifier senderCanAfford(ERC20 token,uint amount) {
       require(token.balanceOf(msg.sender) >= amount);
       _;
   }

   function hasDeposit() returns (uint256){
      return balances[msg.sender].amount;
   }

   // @param timeout == seconds,
   function deposit(ERC20 token, uint256 amount, uint256 timeout)  external{

        balances[msg.sender].amount = amount;
        balances[msg.sender].expiry = now + timeout;
        token.transferFrom(msg.sender,this, amount);

   }

   function withdraw(ERC20 token, uint256 amount)  external {
      // check msg.sender, token_address valid
      // check balance[msg.sender] has balance & balance > amount

       if(balances[msg.sender].expiry > now){
          token.transfer(msg.sender, amount);
          balances[msg.sender].amount -= amount;
          delete balances[msg.sender];
       }
   }

}
